import React, {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css"
import ExpenseFilter from "./ExpenseFilter";

function Expenses(props) {
  const [filterYear, setFilteredYear]=useState('2020');
  const filterChangeHandler=selectedYear=>{
    setFilteredYear(selectedYear);
  }

  const yearFilteredArray = props.items.filter((item)=>{
    return  item.date.getFullYear().toString()===filterYear;
  });

  let expenseContent=<p>No items found.</p>;
  if(yearFilteredArray.legnth > 0){
    expenseContent=yearFilteredArray.map((element) => (
      <ExpenseItem     
        key={element.id}    
        title={element.title}
        amount={element.amount}
        date={element.date}
          />))
          /*Or anather way
           {yearFilteredArray.length===0 ? <p>No items found.</p>:yearFilteredArray.map(element => {      
        return(
          <ExpenseItem     
            key={element.id}    
            title={element.title}
            amount={element.amount}
            date={element.date}
        /> )}
      )}          */
  }

  return (
    <Card className="expenses">    
    <ExpenseFilter selected={filterYear} onChangeFilter ={filterChangeHandler} />   
    {}
    </Card>
  );
}
export default Expenses;
