import { useState } from "react";

const SimpleInput = (props) => {
const [enteredName, setEnteredName]=useState("");
const [enteredNameTouched, setEnteredNameTouched]=useState(false);
const [enteredEmail, setEnteredEmail]=useState("");
const [enteredEmailTouched, setEnteredEmailTouched]=useState(false);

const enteredNameIsValid=enteredName.trim()!=='';
const nameInputIsValid = !enteredNameIsValid && enteredNameTouched;
const enteredEmailIsValid=enteredEmail.trim()!=='';
const emailInputIsValid = !enteredEmailIsValid && enteredEmailTouched;
let mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;


//reads input on every change
const nameInputChangeHandler=(event)=>{
  setEnteredName(event.target.value);
};

const nameInoutBlurHandler=(event)=>{
  setEnteredNameTouched(true);
}
//reads email input on every change
const emailInputChangeHandler=(event)=>{
  setEnteredEmail(event.target.value);

  if(!enteredEmail.match(mailformat)){
    console.log("not much");

  }
};

const emailInoutBlurHandler=(event)=>{
  setEnteredEmailTouched(true);
}

const formSubmissionHandler=(event)=>{
  event.preventDefault();
  setEnteredNameTouched(true);
  setEnteredEmailTouched(true);
  if(!enteredNameIsValid){
     return;
  }
  if(!enteredEmailIsValid){
    return;
 }

  setEnteredName('');
  setEnteredNameTouched(false);
  setEnteredEmail('');
  setEnteredEmailTouched(false);
}

  const nameInputClasses=nameInputIsValid ? "form-control invalid": "form-control"
  const emailInputClasses=emailInputIsValid ? "form-control invalid": "form-control"
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input         
          type='text' id='name' 
          onChange={nameInputChangeHandler}
          onBlur={nameInoutBlurHandler}
        />
        </div>
        <p className="error-text">{nameInputIsValid && "Your input is invalid"}</p>

        <div className={emailInputClasses}>
          <label htmlFor='email'>Your Email</label>
          <input         
            type='email' id='email' 
            onChange={emailInputChangeHandler}
            onBlur={emailInoutBlurHandler}
          />
        </div>
        <p className="error-text">{emailInputIsValid && "Your email is invalid"}</p>

      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
